import React, { useState, useEffect } from 'react';
import { Route, Switch, useHistory } from 'react-router-dom';

import api from '../utils/api';
import * as auth from '../utils/auth';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import EditAvatarPopup from './EditAvatarPopup';
import EditProfilePopup from './EditProfilePopup';
import AddPlacePopup from './AddPlacePopup';
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import Login from './Login';
import Register from './Register';
import ProtectedRoute from './ProtectedRoute';

function App() {

  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [email, setEmail] = useState('');
  // const [message, setMessage] = useState('');
  const [isRequestSuccessful, setRequestSuccessful] = useState(false);

  useEffect(() => {
    Promise.all([api.getProfileInfo(), api.getInitialCards()])
      .then(([user, cards]) => {
        setCurrentUser(user);
        setCards(cards);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

//   useEffect(() => {
//     if (localStorage.getItem('jwt')) {
//       const jwt = localStorage.getItem('jwt');

//       checkToken(jwt)
//         .then((res) => {
//           if(res) {
//             setIsLoggedIn(true);
//             setEmail(res.email); 
//           }
//         })
//         .catch((err) => {
//           openRegModal();
//           console.log(`Произошла ошибка: ${err}`);
//         });
//     }
//   }, [history, loggedIn]);

//   function tokenCheck() {
//     if (localStorage.getItem('jwt')) {
//       const jwt = localStorage.getItem('jwt');
//       if (jwt) {
//         auth.checkToken(jwt)
//         .then((res) => {
//           if (res) {
//             setEmail(res.data.email);
//             setIsLoggedIn(true);
//           }
//         })
//         .catch((err) => {
//             console.log(err);
//         });
//       }
//     }
//   }

//   const history = useHistory();

//   useEffect(() => {
//     tokenCheck();
// }, [isLoggedIn])

function handleRegister({ password, email }) {
  auth.register({ password, email })
    .then(() => {
      setRequestSuccessful(true);
      history.push('/sign-in');
    })
    .catch((err) => {
      setRequestSuccessful(false);
      console.log(err);
    })
    .finally(() => {
      setInfoTooltipPopupOpen(true);
    })
}

  //   const handleLogin = (data) => {
  //     const { password, email } = data;
  //     auth.authorization(password, email)
  //         .then((data) => { 
  //             if (data.token) { //проверяем, есть ли у пришедших данных токен
  //                 localStorage.setItem('jwt', data.token); // сохраняем токен пользователя
  //                 setIsLoggedIn(true);
  //                 history.push('/');
  //             }
  //         })
  //         .catch((err) => {
  //             console.log(err);
  //         });
  // }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleCardClick(card){
    setSelectedCard(card);
  }

  // Функция-обработчик для закрытия всех попапов
  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard({});
  }

    //обновить данные пользователя
  function handleUpdateUser(user) {
    api.editProfile(user.name, user.about)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      });
  }

    //обновить аватар
  function handleUpdateAvatar(avatar) {
    api.editAvatar(avatar)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(err);
      });
  }

    //добавить новую карточку
  function handleAddPlaceSubmit(card) {
    api.addCard(card.name, card.link)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => {
        console.error(err);
      });
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    if (!isLiked) {
      api.addLike(card._id)
        .then((newCard) => {
          setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
        }).catch((err) => {
          console.error(err);
      });
    } else {
      api.removeLike(card._id).then((newCard) => {
        setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
      }).catch((err) => {
        console.error(err);
      });
    }
  }

    function handleCardDelete(card) {
        api.deleteCard(card._id)
            .then(() => {
                setCards((cards) =>
                    cards.filter((c) =>
                        c._id !== card._id
                    )
                )
                closeAllPopups();
            })
            .catch((err) => {
               console.error(err);
            });
    }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page__container">
        <Header />
        <Switch>
          <Route path='/sign-up'>
            <Register onRegister={handleRegister} />
          </Route>
          <Route path='/sign-in'>
            <Login onLogin={handleLogin} />
          </Route>
          <ProtectedRoute exact path="/"
            component = {Main}
            onEditAvatar = {handleEditAvatarClick}
            onEditProfile = {handleEditProfileClick}
            onAddPlace = {handleAddPlaceClick}
            onCardClick={handleCardClick}
            cards={cards}
            onCardLike={handleCardLike}
            onCardDelete={handleCardDelete}
          />
        </Switch>  
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddPlaceSubmit}
        />
        <PopupWithForm
          name="confirm"
          title="Вы уверены?"
          formName="form-confirm"
          submitText="Да"
          onClose={closeAllPopups}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />
        <ImagePopup
          name="image"
          card={selectedCard}
          onClose={closeAllPopups}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
