import React from 'react';
import api from "../utils/api";

function Main(props) {

  React.useEffect(() => {
    api.getUserInfo()
    .then((res) => {
        userId = user._id
        userData.setUserInfo(user.name, user.about);
        userData.setUserAvatar(user.avatar);
      })
    .catch((err) => {
       console.log(err);
      });

  }, []);

return (
    <>
      <main className="content">
        <section className="profile">
          <div className="profile__avatar-wrapper">
            <img className="profile__avatar"
                 onClick={props.onEditAvatar}
                 style={{ backgroundImage: `url(${props.userAvatar})` }}
                 alt="Аватар"
            />
            <button type="button" className="profile__avatar-edit" />
          </div>
          <div className="profile__info">
            <div className="profile__info-wrap">
              <h1 className="profile__title">{props.userName}</h1>
              <button
                type="button"
                className="profile__edit-button"
                onClick={props.onEditProfile}
                aria-label="Редактировать"
              />
            </div>
            <p className="profile__subtitle">{props.userDescription}</p>
          </div>
          <button
            type="button"
            className="profile__add-button"
            onClick={props.onAddPlace}
            aria-label="Добавить"
          />
        </section>
        <section className="elements">
          <ul className="elements__list" />
        </section>

      </main>
    </>
  );
}

export default Main;
