import React from 'react';
import Header from './Header';
import Footer from './Footer';
import Main from './Main';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';


function App() {

  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleCardClick(card){
    setSelectedCard(card);
  }

  // Функция-обработчик для закрытия всех попапов
  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard({});
  }

  return (
    <div className="page__container">
      <>
        <Header />
        <Main
          onEditAvatar = {handleEditAvatarClick}
          onEditProfile = {handleEditProfileClick}
          onAddPlace = {handleAddPlaceClick}
          onCardClick={handleCardClick}
        />
        <Footer />
        <PopupWithForm /* Форма редактирования профиля */
          name="edit"
          title="Редактировать профиль"
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          submitText="Сохранить">
          >
             <input
               className="popup__input"
               type="text"
               placeholder="Имя"
               defaultValue=""
               name="name"
               id="name-input"
               required=""
               minLength={2}
               maxLength={40}
             />
             <span id="name-input-error" className="popup__error" />
             <input
               className="popup__input"
               type="text"
               placeholder="Описание"
               defaultValue=""
               name="description"
               id="job-input"
               required=""
               minLength={2}
               maxLength={200}
             />
             <span id="job-input-error" className="popup__error" />
        </PopupWithForm>

        <PopupWithForm /* Форма редактирования профиля */
          name="edit"
          title="Редактировать профиль"
          formName="form-profile"
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          submitText="Сохранить">
          >
        </PopupWithForm>

        <PopupWithForm /* Форма запрос на удаление "Вы уверены?" */
          name="confirm"
          title="Вы уверены?"
          formName="form-confirm"
          submitText="Да"
        />

        <PopupWithForm /* Форма добавления карточки */
          name="card"
          title="Новое место"
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          submitText="Сохранить">
        </PopupWithForm>

        <ImagePopup /* Форма открытия попапа картинки */
          name="image"
          link={selectedCard}
          onClose={closeAllPopups}
        />

        <PopupWithForm /* Форма обновления аватара */
          name="edit-avatar"
          title="Обновить аватар"
          formName="form-avatar"
          submitText="Сохранить"
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          >
        </PopupWithForm>







      </>
    </div>
  );

}
export default App;
